/**
 * 简化版的Bootstrap Bundle，只提供关键功能
 * 作为CDN加载失败时的备用方案
 */
(function() {
    // 创建全局bootstrap对象
    window.bootstrap = {
        // 简化版的Collapse实现
        Collapse: function(element) {
            this.element = element;
            
            // 简化版的hide方法
            this.hide = function() {
                if (element && element.classList) {
                    element.classList.remove('show');
                    console.log("备用Bootstrap: 已隐藏元素", element);
                }
            };
            
            // 简化版的show方法
            this.show = function() {
                if (element && element.classList) {
                    element.classList.add('show');
                    console.log("备用Bootstrap: 已显示元素", element);
                }
            };
            
            // 简化版的toggle方法
            this.toggle = function() {
                if (element && element.classList) {
                    if (element.classList.contains('show')) {
                        this.hide();
                    } else {
                        this.show();
                    }
                }
            };
            
            console.log("备用Bootstrap: 已初始化Collapse", element);
        }
    };
    
    console.log("备用Bootstrap已加载");
})(); 